import React, {Component} from "react";
import Grid from "react-bootstrap/lib/Grid";
import Row from "react-bootstrap/lib/Row";
import Col from "react-bootstrap/lib/Col";

class Menu extends Component {
  state = {
    beers: []
  };

  loadBeers = () => {

  }

  render() {
    return (
      <div>
        <h1>Menu Page</h1>
        <Grid>
        {this.state.cards.length ? (
          <Row>
            {/* Details for each Credit Card added to card */}
              { this.state.cards.map( card => (
                <Col md={4}>
                  <Card>
                    <Row>
                      <Col md={12} className="name">
                        {card.name}
                      </Col>
                      <Col md={12} className="number">
                        {card.number}
                      </Col>
                      <Col md={6} className="cvv">
                        {card.cvv}
                      </Col>
                      <Col md={6} className="exp">
                        {card.exp}
                      </Col>
                      <ButtonToolBar>
                        <Button bsStyle="danger" onClick={() => this.deleteCard(card._id)} >Delete</Button>
                      </ButtonToolBar>
                    </Row>
                  </Card>
                </Col>
              ))}
              </Row>

            ) : (
              <h3 style={style}> No Beers Listed -- Please check with the Bartender. </h3>
          )}
        </Grid>
      </div>
    );
  }
}

export default Menu;
