import React, {Component} from "react";
import API from "../../utils/API"
import Grid from "react-bootstrap/lib/Grid";
import Row from "react-bootstrap/lib/Row";
import Col from "react-bootstrap/lib/Col";
// import ButtonToolBar from "react-bootstrap/lib/ButtonToolbar";
// import Button from "react-bootstrap/lib/Button";
import {Modal} from "../../components/Modal"
import {Card} from "../../components/Card";


class Admin extends Component {
  state = {
    beers: []
  };

  componentDidMount() {
    this.loadBeers();
  };

  loadBeers = () => {
    API.getBeers()
      .then(res =>
        this.setState({ beers: res.data, breweryName: "", beerName: "", style: "", ibu: "", abv: "" }),
        console.log(this.state.cards)
        )
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  // function to add the beer to Mongo
  addBeer = () => {
    const beerInfo = {
      breweryName: this.state.breweryName,
      beerName: this.state.beerName, 
      style: this.state.style, 
      ibu: this.state.ibu, 
      abv: this.state.abv  
    }
    API.saveCard(beerInfo).then(res => {
        console.log(res, "This is the response from card info");
        if (res.status === 200) {
          document.getElementById("messageLoc").innerHTML = "Card succesfully added";
          this.clearForm();
        }
        else {
          document.getElementById("messageLoc").innerHTML = "There was a problem saving your card";
        }
    });
  };

  render() {
    return (
      <div>
        <br />
        <Grid>
          <Row>
            <Col md={7}>
              <h1>Admin Page</h1> 
            </Col>
            <Col md={5}>
              <Modal 
                buttonName="Add New Beer" 
                modalTitle="Add New Beer"
                breweryName={this.state.breweryName}
                beerName={this.state.beerName}
                abv={this.state.abv}
                ibu={this.state.ibu}
                handleInputChange={this.handleInputChange}
                onClick={() => this.addBeer()}
              >
              </Modal>
            </Col>
          </Row>

          {this.state.beers.length ? (
            <Row>
              {/* Details for each Beer added to card */}
                { this.state.beers.map( beer => (
                  <Col md={12}>
                    <Card>
                      <Row>
                        <Col md={3} className="breweryName">
                          {beer.breweryName}
                        </Col>
                        <Col md={3} className="beerName">
                          {beer.beerName}
                        </Col>
                        <Col md={2} className="style">
                          {beer.style}
                        </Col>
                        <Col md={2} className="abv">
                          {beer.abv}
                        </Col>
                        <Col md={2} className="ibu">
                          {beer.ibu}
                        </Col>
                      </Row>
                    </Card>
                  </Col>
                ))}
            </Row>

          ) : (
            <h3> No Beers Listed -- Please check with the Bartender. </h3>
          )}

        </Grid>
      </div>
    );
  }
}

export default Admin;
